pragma solidity ^0.6.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/// @author Solidity Services
/// @title Galaxis: DAI test
/// @notice simulates DAI token for the unit tests
contract DAITestTKN is ERC20 {

    constructor(string memory _name, string memory _symbol, uint8 _decimals) ERC20(_name,_symbol) public {
        _setupDecimals(_decimals);
    }

    function mint(address account, uint256 amount) public {
        super._mint(account, amount);
    }

}

