pragma solidity ^0.6.0;

/// @author Solidity Services
/// @title Galaxis: Composition  registering a fund manager address
/// @notice Basic logic for fund manager address
contract GX_FundManagerReference {

    /// STATE VARIABLES

    /// @notice administrating of the treasury is the fund manager
    address fundManagerAddress;

    constructor(address _fundManagerAddress) public {
        fundManagerAddress = _fundManagerAddress;
    }

    /// MODIFIERS

    /// @notice isFundManager
    modifier onlyFromFundManager() {
        require((msg.sender == fundManagerAddress) && (isContract(msg.sender)), "Galaxis: It can be called only from the Fund Manager");
        _;
    }

    /// UTILS

    /// @notice checking if calling function is a contract
    /// @dev ...
    /// @param _addr the address to be checked
    function isContract(address _addr) private view returns (bool){
        uint32 size;
        assembly {
            size := extcodesize(_addr)
        }
    return (size > 0);
    }
}

