pragma solidity ^0.6.0;

import '../Governance/GX_GovernanceAdmin.sol';

/// @author Solidity Services
/// @title Galaxis: Registering shares as an external token
/// @notice Registering shares as an external token
contract GX_SharesReference is GX_GovernanceAdmin {

    /// @notice reference of the shares contract
    address public SharesReference;

    /// EVENTS: user facing events
    /// @notice event for setting the shares reference
    event GX_SharesSet(address indexed sharesAddress);

    /// CONSTRUCTOR

    /// @notice functionality for known parites
    /// @dev ...
    constructor() public {
    }

    /// TRANSACTIONS

    /// @notice setting the shares reference
    /// @dev ...
    /// @param _SharesAddress new reference
    function setShares(address _SharesAddress) public onlyAdmin {
        require(SharesReference == address(0),"Galaxis: Shares reference can be set only once");
        SharesReference = _SharesAddress;
        emit GX_SharesSet(_SharesAddress);
    }
}

