pragma solidity ^0.6.0;

import './GX_RuleInterface.sol';
import '../Governance/GX_GovernanceAdmin.sol';

/// @author Solidity Services
/// @title Galaxis: Rules for whitlisting a token in the fund
/// @notice Set whitelist for the fund
contract GX_TokenManager is GX_RuleInterface, GX_GovernanceAdmin {

    /// INTERNALS
    struct TokenInfo {
        uint8 changeRate;
        bool whitelist;
    }

    /// STATE VARIABLES
    /// @notice whitelisted tokens
    mapping (address => TokenInfo) tokens;

    /// EVENTS: user facing events
    /// @notice event for whitelisting a token
    event GX_TokenWhitelisted(address indexed tokenAddress, uint8 _changeRate);
    /// @notice event for blacklisting a token
    event GX_TokenBlacklisted(address indexed tokenAddress);

    /// CONSTRUCTOR

    /// TRANSACTIONS

    /// @notice whitelisting a new token
    /// @dev ...
    /// @param _tokenAddress token to whitelist
    /// @param _changeRate token change rate
    function whitelistToken(address _tokenAddress, uint8 _changeRate) public onlyAdmin {
        tokens[_tokenAddress] = TokenInfo(_changeRate, true);
        emit GX_TokenWhitelisted(_tokenAddress,_changeRate);
    }

    /// @notice blacklisting a token
    /// @dev ...
    /// @param _tokenAddress tokenaddress to blacklist
    function blacklistToken(address _tokenAddress) public onlyAdmin {
        tokens[_tokenAddress].whitelist = false;
        emit GX_TokenBlacklisted(_tokenAddress);
    }

    /// VIEWS

    /// @notice checking if the token is whitelisted
    /// @dev ...
    /// @param _tokenAddress the address to be checked
    function isWhitelisted(address _tokenAddress) public view returns (bool) {
        return tokens[_tokenAddress].whitelist;
    }

    /// @notice getting change rate for a token
    /// @dev ...
    /// @param _tokenAddress the address to be checked
    function getChangeRate(address _tokenAddress) public view returns (uint8) {
        return tokens[_tokenAddress].changeRate;
    }

    /// @notice is it allowed to invest
    /// @dev ...
    function isAllowedDeposit(address _tknDeposit, uint _amount) public override virtual view returns (bool) {
        require(isWhitelisted(_tknDeposit) == true, "Galaxis: Token to deposit is not whitelisted");
    }

    /// @notice is it allowed to withdraw
    /// @dev ...
    function isAllowedWithdraw(address _tknDeposit, uint _amount) public override virtual view returns (bool) {
        require(isWhitelisted(_tknDeposit) == true, "Galaxis: Token to withdraw is not whitelisted");
    }

    /// @notice is it allowed to trade
    /// @dev ...
    function isAllowedPlaceOrder() public override virtual view returns (bool) {
        return true;
    }


}

