pragma solidity ^0.6.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/// @author Solidity Services
/// @title Galaxis: Multitoken treasury
/// @notice Handles token funds
contract GX_Treasury {

    /// STATE VARIABLES

    /// EVENTS

    /// CONSTRUCTORS

    /// @notice creating a treasury
    /// @dev ...
    constructor() public {
    }

    /// MODIFIERS

    /// TRANSACTIONS

    /// @notice Fund manager can transfer ether to the treasury
    function transferTokenTreasury(address _tknAddress, address _to, uint _amount) internal {
        IERC20 token = IERC20(_tknAddress);
        token.transfer(_to,_amount);
    }

    /// @notice Fund manager can approve transfer
    function approveTokenTreasury(address _tknAddress, address _spender, uint _amount) internal {
        IERC20 token = IERC20(_tknAddress);
        token.approve(_spender,_amount);
    }

    /// VIEWS

    /// @notice getting balance of the treasury
    /// @dev ...
    function getBalanceEthTreasury() public view returns (uint) {
        return address(this).balance;
    }

    /// @notice getting token balance of the treasury contract
    /// @dev ...
    function getBalanceTokenTreasury(address _tknAddress) public view returns (uint) {
        IERC20 token = IERC20(_tknAddress);
        return token.balanceOf(address(this));
    }

}

