pragma solidity ^0.6.0;

import '../Governance/GX_GovernanceAdmin.sol';

/// @author Solidity Services
/// @title Galaxis: Registered parties
/// @notice Simple helper functionalities for registered parties
contract GX_TrustedParties is GX_GovernanceAdmin {

    /// @notice parties
    mapping (address => bool) parties;

    /// EVENTS: user facing events
    /// @notice event for adding a party
    event GX_PartyAdded(address indexed partyAddress);
    /// @notice event for removing a party
    event GX_PartyRemoved(address indexed partyAddress);


    /// CONSTRUCTOR

    /// @notice functionality for known parites
    /// @dev ...
    constructor() public {
    }

    /// TRANSACTIONS

    /// @notice adding a new party
    /// @dev ...
    /// @param _newParty new party of the contract
    function addParty(address _newParty) public onlyAdmin {
        parties[_newParty] = true;
        emit GX_PartyAdded(_newParty);
    }

    /// @notice removing a party from the contract
    /// @dev ...
    /// @param _partyToDelete removing the party from the contract
    function removeParty(address _partyToDelete) public onlyAdmin {
        parties[_partyToDelete] = false;
        emit GX_PartyRemoved(_partyToDelete);
    }

    /// VIEWS

    /// @notice checking if the address is a trusted party
    /// @dev ...
    /// @param _addressToCheck the address to be checked
    function isParty(address _addressToCheck) public view returns (bool) {
        return parties[_addressToCheck];
    }
}

