pragma solidity ^0.6.0;

/// @author Solidity Services
/// @title Galaxis: Governance implementation
/// @notice Simple Governance admin ancestor class
contract GX_GovernanceAdmin {

    /// @notice admins
    mapping (address => bool) admins;

    /// EVENTS: user facing events
    /// @notice event for adding an admin
    event GX_AdminAdded(address indexed adminAddress);
    /// @notice event for removing admin
    event GX_AdminRemoved(address indexed adminAddress);


    /// CONSTRUCTOR

    /// @notice creating a simple governance contract and initializing the creator as admin
    /// @dev ...
    constructor() public {
        admins[msg.sender] = true;
    }

    /// MODIFIERS
    /// @notice only admin can execute
    modifier onlyAdmin() {
        require(admins[msg.sender] == true, "Galaxis: Access denied: not an admin of the contract");
        _;
    }

    /// TRANSACTIONS

    /// @notice adding a new admin to the contract
    /// @dev ...
    /// @param _newAdmin new admin of the contract
    function addAdmin(address _newAdmin) public onlyAdmin {
        admins[_newAdmin] = true;
        emit GX_AdminAdded(_newAdmin);
    }

    /// @notice removing an admin from the contract
    /// @dev ...
    /// @param _adminToDelete removing the admin from the contract
    function removeAdmin(address _adminToDelete) public onlyAdmin {
        admins[_adminToDelete] = false;
        emit GX_AdminRemoved(_adminToDelete);
    }

    /// VIEWS

    /// @notice checking if the address is admin
    /// @dev ...
    /// @param _addressToCheck the address to be checked
    function isAdmin(address _addressToCheck) public view returns (bool) {
        return admins[_addressToCheck];
    }

}

