pragma solidity ^0.6.0;

import './GX_GovernanceAdmin.sol';

/// @author Solidity Services
/// @title Galaxis: Governance implementation
/// @notice Simple Governance investor ancestor class
contract GX_GovernanceInvestor is GX_GovernanceAdmin {

    /// @notice investors
    mapping (address => bool) investors;

    /// EVENTS: user facing events
    /// @notice event for adding an investor
    event GX_InvestorAdded(address indexed investorAddress);
    /// @notice event for removing an investor
    event GX_InvestorRemoved(address indexed investorAddress);


    /// CONSTRUCTOR

    /// @notice creating a simple governance contract for investors
    /// @dev ...
    constructor() public {
    }

    /// MODIFIERS
    /// @notice only Investor
    modifier onlyInvestor() {
        require(investors[msg.sender] == true, "Galaxis: Access denied: not an investor of the contract");
        _;
    }

    /// TRANSACTIONS

    /// @notice adding a new investor to the contract
    /// @dev ...
    /// @param _newInvestor new investor of the contract
    function addInvestor(address _newInvestor) public onlyAdmin {
        investors[_newInvestor] = true;
        emit GX_InvestorAdded(_newInvestor);
    }

    /// @notice removing an investor from the contract
    /// @dev ...
    /// @param _investorToDelete removing the investor from the contract
    function removeInvestor(address _investorToDelete) public onlyAdmin {
        investors[_investorToDelete] = false;
        emit GX_InvestorRemoved(_investorToDelete);
    }

    /// VIEWS

    /// @notice checking if the address is an investor
    /// @dev ...
    /// @param _addressToCheck the address to be checked
    function isInvestor(address _addressToCheck) public view returns (bool) {
        return investors[_addressToCheck];
    }

}

