pragma solidity ^0.6.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import '../Composition/GX_FundManagerReference.sol';
import './GX_PortfolioTKNInterface.sol';

/// @author Solidity Services
/// @title Galaxis: Shares of the token portfolio
/// @notice Handles shares in a token portfolio
contract GX_SharesTKN is ERC20, GX_FundManagerReference, GX_PortfolioTKNInterface {

    /// STATE VARIABLES

    /// CONSTRUCTORS

    constructor(string memory _name, string memory _symbol, uint8 _decimals, address _fundManagerAddress)
    ERC20(_name,_symbol)
    GX_FundManagerReference(_fundManagerAddress) public {
        _setupDecimals(_decimals);
    }

    /// TRANSACTIONS

    /// @notice mint new token
    /// @dev ...
    /// @param _account account to mint
    /// @param _amount amount to mint
    function mint(address _account, uint256 _amount) public override onlyFromFundManager {
        super._mint(_account, _amount);
    }

    /// @notice burn token
    /// @dev ...
    /// @param _account account from burn
    /// @param _amount amount to burn
    function burn(address _account, uint256 _amount) public override onlyFromFundManager {
        super._burn(_account, _amount);
    }

}

